mapper.select:
SELECT 

FROM ad_user a

WHERE a.id = #{id}

SELECT 

FROM ad_user a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.group_num = #{groupNum}


		AND a.card_number = #{cardNumber}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.is_active = #{isActive}


		AND a.type = #{type}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM ad_user a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE ad_user SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
